mapper.select:
SELECT *
FROM user
WHERE email = #{email} AND social_name=#{socialType}

/* 회원 정보 조회 */ 
SELECT * 
	FROM user
WHERE id = #{id}

mapper.update:
/* 회원 정보 수정 */ 
UPDATE user SET

	name = #{name},


	password = #{password}

WHERE id = #{id}

mapper.delete:
/*회원 정보 삭제*/
DELETE 
	FROM user
WHERE id = #{id}

mapper.select:
/*회원 리스트 총갯수*/
SELECT COUNT(*)
	FROM user
